		throw new NotFoundError({
			message: 'Could not find course with id:' + params.id
		})
	}

	ctx.status = 200
	ctx.body = { course }
}

async function createCourse(ctx) {
	const courseData = ctx.request.body.course

	const newCourse = await models.Course.create(courseData)

	ctx.status = 200
